import React, { useState, useEffect } from "react";
import { motion } from "framer-motion";

import { LogoMobile, LogoDesktop, MenuIcon, CrossIcon } from "../../icons";
import navigationOptions from "./utils/navigationOptions";
import styles from "./Navbar.module.scss";

function Navbar() {
  const [toggle, setToggle] = useState(false);
  const [realHeight, setRealHeight] = useState(0);

  const IconMenu = toggle ? CrossIcon : MenuIcon;

  useEffect(() => {
    const bodyHeight = document.body.scrollHeight;
    setRealHeight(bodyHeight);

    const getBodyHeight = () => {
      const bodyHeight = document.body.scrollHeight;
      setRealHeight(bodyHeight);
    };

    window.addEventListener("resize", getBodyHeight);

    return () => window.removeEventListener("resize", getBodyHeight);
  }, []);

  return (
    <nav className={styles.navbarWrapper}>
      <div className={styles.navbarlogo}>
        <LogoMobile className={styles.logoMobile} />
        <LogoDesktop className={styles.logoDesktop} />
      </div>
      <ul className={styles.navbarLinks}>
        {navigationOptions.map(({ id, linkContainer, title }) => (
          <li key={id}>
            <a href={`#${linkContainer}`}>{title}</a>
          </li>
        ))}
      </ul>

      {/* mobile */}

      <div className={styles.navbarMenu}>
        <IconMenu
          onClick={() => {
            window.scrollTo({ top: 0, behavior: "smooth" });
            setToggle((prev) => !prev);
          }}
        />

        {toggle && (
          <>
            <div
              className={styles.test}
              style={{ height: realHeight ? "100%" : realHeight + "px" }}
            />
            <motion.div
              className={styles.navbarSlide}
              //   x start animation from - 300
              whileInView={{ x: [300, 0] }}
              transition={{ duration: 0.85, ease: "easeOut" }}
            >
              <ul>
                {navigationOptions.map(({ id, linkContainer, title }) => (
                  <li key={id}>
                    <a
                      href={`#${linkContainer}`}
                      onClick={() => setToggle(false)}
                    >
                      {title}
                    </a>
                  </li>
                ))}
              </ul>
            </motion.div>
          </>
        )}
      </div>
    </nav>
  );
}

export default Navbar;
