import React, { useState, useEffect } from "react";
import SwiperCore, { Pagination } from "swiper";
import { Swiper, SwiperSlide } from "swiper/react";
import { wemanRoom, manRoom, childRoom } from "../../data/priceContainer";
import StyledSelect from "../../components/shared/styledSelect";
import Heading from "../../components/typography/heading";
import Text from "../../components/typography/text";
import { StripIcon } from "../../icons";
import {} from './utils/serviceOptions'
import styles from "./Services.module.scss";

SwiperCore.use([Pagination]);

const Services = () => {
  const [currentTab, setCurrentTab] = useState(wemanRoom);
  const [currentSrvice, setCurrentService] = useState(null);
  const [currentPrice, setCurrentPrice] = useState(null);

  useEffect(() => {
    setCurrentService(currentTab.attrs[0]);
  }, [currentTab]);

  useEffect(() => {
    setCurrentPrice(currentSrvice?.subCategories[0]);
  }, [currentSrvice]);

  const carouselSettings = {
    spaceBetween: 20,
    // slidesPerView: 1.5,
    slidesPerView: "auto",
  };

  const carouselSettingsPrice = {
    spaceBetween: 20,
    slidesPerView: 1,
    pagination: {
      el: ".swiper-pagination",
      clickable: true,
    },
  };

  const elL = Math.ceil(currentPrice?.services.attributes.length / 7);
  const newEl = [];

  for (let i = 0; i < elL; i++) {
    const el = currentPrice?.services.attributes.slice(i * 7, i * 7 + 7);
    newEl.push(el);
  }

  return (
    <div id="price" className={styles.wrapper}>
      <Heading
        size="l"
        letterSpacing="5"
        transform="uppercase"
        margin="0 0 36px 0"
        weight="bold"
        color="green"
      >
        НАШІ ПОСЛУГИ ТА ЦІНИ
      </Heading>
      <div className={styles.mainOptionBlock}>
        <div className={styles.subMainOptionBlock}>
          <Text
            size="ss"
            color="#1B4332"
            transform="uppercase"
            letterSpacing="3"
            onClick={() => {
              setCurrentTab(wemanRoom);
            }}
          >
            Жіночий зал
          </Text>
          {currentTab.label === "Жіночий зал" && (
            <div className={styles.underline} />
          )}
        </div>

        <div className={styles.subMainOptionBlock}>
          <Text
            size="ss"
            color="#1B4332"
            transform="uppercase"
            letterSpacing="3"
            onClick={() => {
              setCurrentTab(manRoom);
            }}
          >
            Чоловічий зал
          </Text>
          {currentTab.label === "Чоловічий зал" && (
            <div className={styles.underline} />
          )}
        </div>

        <div className={styles.subMainOptionBlock}>
          <Text
            size="ss"
            color="#1B4332"
            transform="uppercase"
            letterSpacing="3"
            onClick={() => {
              setCurrentTab(childRoom);
            }}
          >
            Дитячий зал (до 12 років)
          </Text>
          {currentTab.label === "Дитячий зал (до 12 років)" && (
            <div className={styles.underline} />
          )}
        </div>
      </div>
      <StyledSelect
        currentOption={currentSrvice}
        options={currentTab.attrs}
        setCurrentOption={setCurrentService}
        marginBottom="30"
      />
      <div className={styles.subservicesWrapper}>
        {!!currentSrvice?.subCategories.filter((el) => el.subCategorie !== "")
          .length && (
          <Swiper {...carouselSettings} freeMode>
            {currentSrvice.subCategories.map((item) => {
              const { id, subCategorie } = item;

              return (
                <SwiperSlide key={id}>
                  <div
                    style={{
                      display: "flex",
                      alignItems: "center",
                      width: "max-content",
                    }}
                  >
                    <StripIcon className={styles.stripIconStyles} />
                    <Text
                      size="s"
                      letterSpacing="2"
                      color="#1B4332"
                      onClick={() => {
                        setCurrentPrice(item);
                      }}
                      whiteSpace="nowrap"
                    >
                      {subCategorie}
                    </Text>
                  </div>
                </SwiperSlide>
              );
            })}
          </Swiper>
        )}
      </div>

      {newEl.length && (
        <div
          style={{
            marginTop: "20px",
            backgroundColor: "#fff",
            paddingBottom: "25px",
            paddingTop: "20px",
          }}
        >
          <Swiper {...carouselSettingsPrice}>
            {newEl.map((item, index) => {
              return (
                <SwiperSlide key={index}>
                  <table style={{ width: "100%" }}>
                    {item.map(({ name, price, label }) => {
                      return (
                        <tr
                          style={{
                            lineHeight: "20px",
                          }}
                        >
                          <td className={styles.tableDataName}>
                            <Text
                              color="#1B4332"
                              size="s"
                              weight={label ? "semibold" : "normal"}
                            >
                              {name.slice(0, 1).toUpperCase() + name.slice(1)}
                            </Text>
                          </td>

                          <td className={styles.tableDataPrice}>
                            <Text
                              color="#1B4332"
                              size="s"
                              weight={label ? "semibold" : "normal"}
                            >
                              {price}
                            </Text>
                          </td>
                        </tr>
                      );
                    })}
                  </table>
                </SwiperSlide>
              );
            })}
            <div
              style={{
                visibility:
                  !newEl.length || newEl.length === 1 ? "hidden" : "visible",
                marginTop: (!newEl.length || newEl.length === 1) && "0px",
              }}
              className="swiper-pagination"
            />
          </Swiper>
        </div>
      )}
    </div>
  );
};

export default Services;
